mapper.select:
select

  distinct

'true' as QUERYID,

from merchant_info




  order by ${orderByClause}


  limit #{page.begin} , #{page.length}

mapper.insert:
insert into merchant_info (full_name, short_name, business_scop, 
  city, address, email, 
  phone_number, mobile_number, service_hotline, 
  qq, operator_id, create_time
  )
values (#{fullName,jdbcType=VARCHAR}, #{shortName,jdbcType=VARCHAR}, #{businessScop,jdbcType=VARCHAR}, 
  #{city,jdbcType=VARCHAR}, #{address,jdbcType=VARCHAR}, #{email,jdbcType=VARCHAR}, 
  #{phoneNumber,jdbcType=VARCHAR}, #{mobileNumber,jdbcType=VARCHAR}, #{serviceHotline,jdbcType=VARCHAR}, 
  #{qq,jdbcType=VARCHAR}, #{operatorId,jdbcType=VARCHAR}, #{createTime,jdbcType=TIMESTAMP}
  )
