mapper.select:
select

  distinct

'true' as QUERYID,

from users




  order by ${orderByClause}


  limit #{page.begin} , #{page.length}

mapper.insert:
insert into users (username, password, name, 
  email, phone, status, 
  parent_id, create_time, login_ip, 
  login_address, login_time, last_login_time
  )
values (#{username,jdbcType=VARCHAR}, #{password,jdbcType=VARCHAR}, #{name,jdbcType=VARCHAR}, 
  #{email,jdbcType=VARCHAR}, #{phone,jdbcType=VARCHAR}, #{status,jdbcType=INTEGER}, 
  #{parentId,jdbcType=INTEGER}, #{createTime,jdbcType=TIMESTAMP}, #{loginIp,jdbcType=VARCHAR}, 
  #{loginAddress,jdbcType=VARCHAR}, #{loginTime,jdbcType=TIMESTAMP}, #{lastLoginTime,jdbcType=TIMESTAMP}
  )
